import React, { useState } from "react";
import "./header.css";
import { Link } from "react-router-dom";

const Header = () => {
  const [active, setActive] = useState(false);

  function toggleClass() {
    if (active === false) {
      setActive(true);
    } else {
      setActive(false);
    }
  }

  return (
    <header>
      <h1 className="logo">
        <Link to="/">
          Session<span>Tracker</span>
        </Link>
      </h1>

      <nav>
        <ul className={active === true ? "nav-links nav-active" : "nav-links"}>
          <li onClick={toggleClass}>
            <Link to="/">Home</Link>
          </li>
          <li>
            <Link to="/clients">Clients</Link>
          </li>
          <li>
            <Link to="/current">Current</Link>
          </li>
        </ul>
      </nav>
      <div onClick={toggleClass} className="burger">
        <div className={active === true ? "toggle line1" : "line1"}></div>
        <div className={active === true ? "toggle line2" : "line2"}></div>
        <div className={active === true ? "toggle line3" : "line3"}></div>
      </div>
    </header>
  );
};

export default Header;
