import React, { useState } from "react";
import "./header.scss";
import logo from "../../assets/R4 60.png";
import { Link } from "react-router-dom";

const Header = () => {
  const [active, setActive] = useState(false);

  function toggleClass() {
    if (active === false) {
      setActive(true);
    } else {
      setActive(false);
    }
  }

  return (
    <header>

        <Link to="/">
        <img className="logo" src={logo} alt="R4 Resources Logo" /> <span><h1>Resources</h1></span>
        </Link>

      <nav>
        <ul className={active === true ? "nav-links nav-active" : "nav-links"}>
          <li onClick={toggleClass}>
            <Link to="/">Home</Link>
          </li>
          <li onClick={toggleClass}>
            <Link to="/experience">Experience</Link>
          </li>
          <li onClick={toggleClass}>
            <Link to="/customers">Customers</Link>
          </li>
          <li onClick={toggleClass}>
            <Link to="/consultants">Consultants</Link>
          </li>
          <li onClick={toggleClass}>
            <Link to="/contact">Contact Us</Link>
          </li>
        </ul>
      </nav>
      <div onClick={toggleClass} className="burger">
        <div className="line1"></div>
        <div className="line2"></div>
        <div className="line3"></div>
      </div>
    </header>
  );
};

export default Header;
